mapper.select:
SELECT

FROM games
WHERE type = #{type,jdbcType=VARCHAR}
LIMIT #{skipRows}, #{pageSize}

SELECT COUNT(game_id)
FROM games
WHERE type = #{type}

SELECT

FROM games
WHERE game_name = #{applyingGame.gameName}
AND developer = #{applyingGame.developer.userId}

mapper.insert:

    SELECT LAST_INSERT_ID()

insert into games (game_id, game_name, type, developer,
price, version, state,
on_sale_date, cover, introduction
)
values (#{gameId,jdbcType=INTEGER}, #{gameName,jdbcType=VARCHAR}, #{type,jdbcType=VARCHAR},
#{developer.userId,jdbcType=INTEGER}, #{price,jdbcType=DOUBLE}, #{version,jdbcType=VARCHAR},
#{state,jdbcType=SMALLINT}, #{onSaleDate,jdbcType=DATE},
#{cover,jdbcType=LONGVARBINARY}, #{introduction,jdbcType=VARCHAR}
)

insert into games_on_applying (game_name, type, developer, price, version, state, cover, introduction)
values (#{gameName,jdbcType=VARCHAR}, #{type,jdbcType=VARCHAR},
#{developer.userId,jdbcType=INTEGER}, #{price,jdbcType=DOUBLE}, #{version,jdbcType=VARCHAR},
#{state,jdbcType=SMALLINT}, #{cover,jdbcType=LONGVARBINARY}, #{introduction,jdbcType=VARCHAR}
)

mapper.update:
UPDATE games_on_applying
SET state = #{state}
WHERE game_id = #{applyId}

UPDATE games
SET state = #{state}
WHERE game_id = #{applyId}

UPDATE games
SET version      = #{applyingGame.version},
    cover        = #{applyingGame.cover},
    introduction = #{applyingGame.introduction}
WHERE game_name = #{applyingGame.gameName}
      AND developer = #{applyingGame.developer.userId}
